import { defineStore } from 'pinia'
import { useProductStore } from './product.js' // 商品情報を管理するストアをインポート

export const useCartStore = defineStore({
  id: 'cart',
  state: () => ({
    cart: [],
  }),
  getters: {
    totalAmount() {
      return this.cart.reduce((acc, curr) => acc + curr.price * curr.quantity, 0)
    },
  },
  actions: {
    addToCart(productId, quantity = 1) {
      const productStore = useProductStore() // 商品情報を管理するストアを使用
      const product = productStore.getProductById(productId) // 商品情報を取得
      if (!product || product.stock < quantity) {
        // 商品が存在しない、または在庫が足りない場合は追加しない
        return
      }

      const productInCart = this.cart.find((item) => item.product_id === productId)
      if (productInCart) {
        productInCart.quantity += quantity
      } else {
        this.cart.push({ ...product, quantity })
      }
    },
    decreaseQuantity(productId) {
      const productInCart = this.cart.find((item) => item.product_id === productId)
      if (productInCart && productInCart.quantity > 0) {
        productInCart.quantity--
      }
    },
    increaseQuantity(productId) {
      const productStore = useProductStore()
      const product = productStore.getProductById(productId)
      const productInCart = this.cart.find((item) => item.product_id === productId)
      if (productInCart && productInCart.quantity < product.stock) {
        productInCart.quantity++
      }
    },
  },
})
