import { defineStore } from 'pinia'
import { useProductStore } from './product.js'

export const useCartStore = defineStore({
  id: 'cart',
  state: () => ({
    cart: [],
  }),
  getters: {
    totalAmount() {
      return this.cart.reduce((acc, curr) => acc + curr.price * curr.quantity, 0)
    },
    quantityInCart: (state) => (productId) => {
      const productInCart = state.cart.find((product) => product.product_id === productId)
      return productInCart ? productInCart.quantity : 0
    },
    totalQuantity() {
      return this.cart.reduce((acc, product) => acc + product.selectedQuantity, 0)
    },
  },
  actions: {
    addToCart(productId, quantity = 1) {
      const productStore = useProductStore()
      const product = productStore.getProductById(productId)
      if (!product) {
        return
      }
      const productInCart = this.cart.find((item) => item.product_id === productId)

      const selectedQuantity = productInCart ? productInCart.selectedQuantity : 0
      const updatedQuantity = selectedQuantity + Number(quantity)
      if (updatedQuantity > product.stock) {
        console.error('Cannot add more items than available in stock.')
        return
      }
      if (productInCart) {
        // カート内に同じ商品が存在する場合は、その商品の数量を指定の数量に加える
        if (updatedQuantity <= product.stock) {
          productInCart.selectedQuantity = updatedQuantity
        } else {
          console.error('Cannot add more items than available in stock.')
          return
        }
      } else {
        // カート内にその商品がない場合は、新しく商品を追加
        if (quantity <= product.stock) {
          this.cart.push({ ...product, quantity: Number(quantity), selectedQuantity: Number(quantity) })
        } else {
          console.error('Cannot add more items than available in stock.')
          return
        }
      }
    },
    // 既存の decreaseQuantity, increaseQuantity メソッドの selectedQuantity 対応
    decreaseQuantity(productId) {
      const productInCart = this.cart.find((item) => item.product_id === productId)
      if (productInCart && productInCart.selectedQuantity > 0) {
        productInCart.selectedQuantity--
      }
    },
    increaseQuantity(productId) {
      const productStore = useProductStore()
      const product = productStore.getProductById(productId)
      const productInCart = this.cart.find((item) => item.product_id === productId)
      if (productInCart && productInCart.selectedQuantity < product.stock) {
        productInCart.selectedQuantity++
      }
    },
    // 既存の setQuantity メソッドの selectedQuantity 対応
    setQuantity(productId, quantity) {
      const productInCart = this.cart.find((item) => item.product_id === productId)
      if (productInCart) {
        productInCart.selectedQuantity = quantity
      }
    },
    // 新しいメソッドとして updateSelectedQuantity を追加
    updateSelectedQuantity(productId, selectedQuantity) {
      const productInCart = this.cart.find((item) => item.product_id === productId)
      if (productInCart) {
        productInCart.selectedQuantity = selectedQuantity
      }
    },
    // 既存の removeProduct メソッドはそのまま
    removeProduct(productId) {
      const index = this.cart.findIndex((item) => item.product_id === productId)
      if (index !== -1) {
        this.cart.splice(index, 1)
      }
    },
  },
})
