import { defineStore } from 'pinia'

export const useUserStore = defineStore({
  id: 'userStore',

  state: () => ({
    userId: null,
    password: null,
    username: null,
  }),

  actions: {
    async login(userId, password) {
      const response = await fetch(
        'https://script.google.com/macros/s/AKfycbxUGsnRZlCSVZDE3Qinekojhfy3EyDcYRR8q6zWgDt4MxP7EX0PtV10_CfNXX2J7A4CLA/exec'
      )
      const data = await response.json()

      const user = data.find((user) => user.user_id === userId && user.password === password)

      if (user) {
        this.userId = userId
        this.password = password
        this.username = user.username
        return true
      } else {
        return false
      }
    },

    logout() {
      this.userId = null
      this.password = null
      this.username = null
    },
  },
})
