import { defineStore } from 'pinia'
import axios from 'axios'

export const useUserStore = defineStore({
  id: 'user',
  state: () => ({
    users: [],
    loggedInUser: null,
  }),
  actions: {
    async fetchUsers() {
      try {
        const response = await axios.get('https://script.googleusercontent.com/macros/echo?user_content_key=7OesvJVrX1zT2fJp2F74toAQuoDqiW_Zs4bHkSmIIWBh6IqPpgu6haD7a6L0rU3ML-YdH48hNJSdAqJgKy_gi_Gnsv2-E4Xrm5_BxDlH2jW0nuo2oDemN9CCS2h10ox_1xSncGQajx_ryfhECjZEnF25j0Jy-Bfh3wqQJbIbcCixgfLNQygasURqu7MuhCyP1I1xAABVsFil5BdwSEbXp-Orcpqi59zaFOPoEBZJbQcoZN8ak6ICjtz9Jw9Md8uu&lib=Mtn_vs8cSZ0a4v_3WWGjVuMZHbvKLxQr5')
        this.users = response.data
      } catch (error) {
        console.error('ユーザーデータを取得できませんでした。', error)
      }
    },

    login(username, password) {
      const user = this.users.find((user) => user.username === username && user.password === password)
      if (user) {
        this.loggedInUser = user
      } else {
        alert('ユーザー名またはパスワードが正しくありません。')
      }
    },
    logout() {
      this.loggedInUser = null
    },
  },
})
