import { defineStore } from 'pinia'
import axios from 'axios'

export const useStore = defineStore({
  id: 'product',
  // stateにproductsの箱とcartの箱を用意
  state: () => ({
    products: [],
    cart: {},
  }),
  actions: {
    async fetchProducts() {
      const response = await axios.get(
        'https://script.googleusercontent.com/macros/echo?user_content_key=Em_KQKtY2OT34rNsLMthkfcZB33Cd4IpZcfEgGSHMJLr560yyr4jS-EO8GH_aSCQumn49I8bxHSdoIJVnZ-u1yXsAElIzlLFm5_BxDlH2jW0nuo2oDemN9CCS2h10ox_1xSncGQajx_ryfhECjZEnK4jwdgwZpo7gx_qVnURw9R5JFnetyDRT0j1vZkq91aylQdCZZjHUQaoRM1HWPX5NcE1Fr3LGjCqbMVgMdOpVtqaJbRBv9gJFtz9Jw9Md8uu&lib=Mtn_vs8cSZ0a4v_3WWGjVuMZHbvKLxQr5'
      )
      this.products = response.data
    },
    addToCart(productId, quantity) {
      if (!this.cart[productId]) {
        this.cart[productId] = 0
      }
      this.cart[productId] += quantity
      const product = this.products.find((item) => item.product_id === productId)
      if (product) {
        product.stock -= quantity
      }
    },
  },
})
