import { defineStore } from 'pinia'

export const useCartStore = defineStore({
  id: 'cart',

  state: () => ({
    items: [],
  }),

  actions: {
    addItem(productId) {
      this.items.push(productId)
    },

    removeItem(productId) {
      const index = this.items.indexOf(productId)
      if (index !== -1) {
        this.items.splice(index, 1)
      }
    },

    emptyCart() {
      this.items = []
    },
  },
})
