import { defineStore } from 'pinia'
import axios from 'axios'

export const useProductStore = defineStore({
  id: 'product',
  state: () => ({
    products: [],
  }),
  actions: {
    async fetchProducts() {
      const response = await axios.get(
        'https://script.googleusercontent.com/macros/echo?user_content_key=Em_KQKtY2OT34rNsLMthkfcZB33Cd4IpZcfEgGSHMJLr560yyr4jS-EO8GH_aSCQumn49I8bxHSdoIJVnZ-u1yXsAElIzlLFm5_BxDlH2jW0nuo2oDemN9CCS2h10ox_1xSncGQajx_ryfhECjZEnK4jwdgwZpo7gx_qVnURw9R5JFnetyDRT0j1vZkq91aylQdCZZjHUQaoRM1HWPX5NcE1Fr3LGjCqbMVgMdOpVtqaJbRBv9gJFtz9Jw9Md8uu&lib=Mtn_vs8cSZ0a4v_3WWGjVuMZHbvKLxQr5'
      )
      this.products = response.data
    },
  },
  getters: {
    getProductById: (state) => (id) => {
      return state.products.find((product) => product.product_id === id)
    },
  },
})
