import { defineStore } from 'pinia'

export const usePaymentStore = defineStore({
  id: 'payment',
  state: () => ({
    selectedAddress: null,
  }),
  getters: {
    selectedAddress: (state) => state.selectedAddress,
  },
  actions: {
    setSelectedAddress(address) {
      this.selectedAddress = address
    },
  },
})
