import { defineStore } from 'pinia'

export const useFavoriteStore = defineStore('favorite', {
  state: () => ({
    favoriteQuantities: 1,
  }),
  actions: {
    setFavoriteQuantities(quantity) {
      this.favoriteQuantities = quantity
    },
  },
})
