import { defineStore } from 'pinia'

export const useStore = defineStore({
  // ストアの一意なID
  id: 'userStore',

  // 状態を定義
  state: () => ({
    userId: null,
    password: null,
    username: null, // ユーザー名を保存するためのプロパティ
  }),

  // アクションを定義
  actions: {
    async login(userId, password) {
      // JSONデータを取得
      const response = await fetch('https://script.google.com/macros/s/AKfycbxUGsnRZlCSVZDE3Qinekojhfy3EyDcYRR8q6zWgDt4MxP7EX0PtV10_CfNXX2J7A4CLA/exec')
      const data = await response.json()

      // ユーザーを見つける
      const user = data.find(user => user.user_id === userId && user.password === password)

      // ユーザーが見つかった場合、その情報を状態に保存
      if(user) {
        this.userId = userId
        this.password = password
        this.username = user.username // ユーザー名を保存
        return true
      } else {
        // ユーザーが見つからなかった場合
        return false
      }
    },

    logout() {
      // ログアウト時は全ての情報をnullに戻す
      this.userId = null
      this.password = null
      this.username = null
    },
  },
})
