import { defineStore } from 'pinia'

export const useFavoriteStore = defineStore('favorite', {
  state: () => ({
    favoriteQuantities: [],
  }),
  actions: {
    setFavoriteQuantities(selectedProduct, quantity) {
      const index = this.favoriteQuantities.findIndex((item) => item.product_id === selectedProduct.product_id)
      if (index !== -1) {
        // 既にお気に入りに追加されている商品なら、情報を更新
        this.favoriteQuantities[index] = { ...selectedProduct, ...quantity }
      } else {
        // 新しいお気に入り商品なら配列に追加
        this.favoriteQuantities.push({ ...selectedProduct, ...quantity })
      }
    },
    removeFavoriteQuantities(selectedProduct) {
      this.favoriteQuantities = this.favoriteQuantities.filter((item) => item.product_id !== selectedProduct.product_id)
    },
    liftFavoriteQuantities(product) {
      const index = this.favoriteQuantities.findIndex((item) => item.product_id === product.product_id)
      if (index !== -1) {
        // 該当の商品を配列から削除
        this.favoriteQuantities.splice(index, 1)
      }
    },
  },
})
