import { defineStore } from "pinia";
import axios from "axios";

export const useProductStore = defineStore({
  id: "product",
  state: () => ({
    products: [],
  }),
  actions: {
    async fetchProducts() {
      const response = await axios.get(
        "https://script.googleusercontent.com/macros/echo?user_content_key=4fT7I0aZp6B0Xl95h1Y7BaLKuGpmJysdQL5oZPCYcsszYk9nCTGs6acFM9wT4F8z2qWnPsb39Z_L5_tDcK5LTSAgG00HhnGCm5_BxDlH2jW0nuo2oDemN9CCS2h10ox_1xSncGQajx_ryfhECjZEnLKLFCbu9NV3CzIe2ROWPuOu5EgWn0P_23G2jxEGiFJ0PAbitHcTPhZAPNcMo2oFxjEVMb8Ie2lQq9grjmZvi_V_nhEG9FW4jQ&lib=MfcnrPelN8U0JStyng4jmGtucTm4bx3Zi"
      );
      this.products = response.data;
    },
  },
  getters: {
    getProductById: (state) => (id) => {
      return state.products.find((product) => product.product_id === id);
    },
  },
});
