import { defineStore } from 'pinia'

export const useCartStore = defineStore({
  id: 'cart',
  state: () => ({
    cart: [],
  }),
  getters: {
    totalAmount() {
      return this.cart.reduce((acc, curr) => acc + curr.price * curr.quantity, 0)
    },
  },
  actions: {
    addToCart(product, quantity = 1) {
      const productInCart = this.cart.find((item) => item.product_id === product.product_id)
      if (productInCart) {
        productInCart.quantity += quantity
      } else {
        this.cart.push({ ...product, quantity })
      }
    },
    decreaseQuantity(product) {
      const productInCart = this.cart.find((item) => item.product_id === product.product_id)
      if (productInCart && productInCart.quantity > 0) {
        productInCart.quantity--
      }
    },
    increaseQuantity(product) {
      const productInCart = this.cart.find((item) => item.product_id === product.product_id)
      if (productInCart && productInCart.quantity < product.stock) {
        productInCart.quantity++
      }
    },
  },
})
